import React, { useState } from 'react';
import axios from 'axios';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import './style.css'; // Import external CSS file

const validationSchema = Yup.object().shape({
  name: Yup.string().required('Name is required'),
  phoneNumber: Yup.string().required('Phone Number is required'),
  email: Yup.string().email('Invalid email').required('Email is required'),
  companyName: Yup.string(),
  address: Yup.string(),
  howDidYouHear: Yup.string().required('How did you hear about us is required'),
  inquiry: Yup.string().required('Inquiry is required')
});

const initialValues = {
  name: '',
  phoneNumber: '',
  email: '',
  companyName: '',
  address: '',
  howDidYouHear: '',
  inquiry: ''
};

const BeautifulForm = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = async (values, { resetForm }) => {
    setIsLoading(true);
    setIsSubmitted(false);

    // Simulate a delay of 10 seconds for loading
    setTimeout(async () => {
      try {
        // Simulate a delay for sending data
        await new Promise(resolve => setTimeout(resolve, 10000));

        const response = await axios.post(
          'http://localhost:5000/submit', // Replace with your backend service URL
          values
        );

        console.log(response.data); // Log response for debugging
        setIsSubmitted(true);
        resetForm();
      } catch (error) {
        console.error('Error submitting form:', error);
      } finally {
        setIsLoading(false);
      }
    }, 10000);
  };

  const handleClear = (resetForm) => {
    resetForm();
    setIsSubmitted(false);
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Contact Us</h2>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ isSubmitting, resetForm }) => (
          <Form className={`form ${isSubmitted ? 'submitted' : ''}`}>
            <div className={`form-group-grid ${isSubmitted ? 'hidden' : ''}`}>
              <div className="form-group">
                <Field name="name" className="form-field" placeholder="Name" />
                <ErrorMessage name="name" component="div" className="error-message" />
              </div>
              <div className="form-group">
                <Field name="phoneNumber" className="form-field" placeholder="Phone Number" />
                <ErrorMessage name="phoneNumber" component="div" className="error-message" />
              </div>
              <div className="form-group">
                <Field name="email" className="form-field" placeholder="Email Address" />
                <ErrorMessage name="email" component="div" className="error-message" />
              </div>
              <div className="form-group">
                <Field name="companyName" className="form-field" placeholder="Company Name" />
                <ErrorMessage name="companyName" component="div" className="error-message" />
              </div>
            </div>
            <div className={`form-group ${isSubmitted ? 'hidden' : ''}`}>
              <Field name="address" className="form-field" placeholder="Address" />
              <ErrorMessage name="address" component="div" className="error-message" />
            </div>
            <div className={`form-group ${isSubmitted ? 'hidden' : ''}`}>
              <Field as="select" name="howDidYouHear" className="form-field">
                <option value="" label="How did you hear about us" />
                <option value="google" label="Google" />
                <option value="facebook" label="Facebook" />
                <option value="twitter" label="Twitter" />
                <option value="linkedin" label="LinkedIn" />
              </Field>
              <ErrorMessage name="howDidYouHear" component="div" className="error-message" />
            </div>
            <div className={`form-group ${isSubmitted ? 'hidden' : ''}`}>
              <Field as="textarea" name="inquiry" className="form-field" placeholder="How can we help you?" />
              <ErrorMessage name="inquiry" component="div" className="error-message" />
            </div>
            <div className={`form-group ${isSubmitted ? '' : 'hidden'}`}>
              {isLoading ? (
                <p className="loading-message">Submitting...</p>
              ) : (
                <p className="success-message">Form submitted successfully!</p>
              )}
            </div>
            <div className={`form-group ${isSubmitted ? 'hidden' : ''}`}>
              <button type="submit" className="form-button" disabled={isSubmitting || isLoading}>
                {isLoading ? 'Submitting...' : 'Submit'}
              </button>
              <button
                type="button"
                className="clear-button"
                onClick={() => handleClear(resetForm)}
                disabled={isSubmitting || isLoading}
              >
                Clear
              </button>
            </div>
          </Form>
        )}
      </Formik>
      {showThankYouMessage && (
        <div className="thank-you-message">
          <p>We have received your query. We will contact you soon. Thank you for choosing us.</p>
        </div>
      )}
    </div>
  );
};

export default BeautifulForm;
